import { ArrowLeftOutlined, ArrowRightOutlined } from "@material-ui/icons";
import React from "react";
import styled from "styled-components";

const Container = styled.div`
	width: 100%;
	height: 100vh;
	display: flex;
	background-color: yellow;
	position: relative;
`;

const Arrow = styled.div`
	width: 50px;
	height: 50px;
	background-color: #f1f1f1;
	border-radius: 50%;
	display: flex;
	align-items: center;
	justify-content: center;
	position: absolute;
	top: 0;
	left: ${(props) => props.direction === "left" && "10px"};
	right: ${(props) => props.direction === "right" && "10px"};
	bottom: 0;
	margin: auto;
	cursor: pointer;
	opacity: 0.5;
`;

const Wrapper = styled.div`
	height: 100%;
`;

const Slide = styled.div`
	width: 100vw;
	height: 100vh;
	display: flex;
	align-itens: center;
`;

const ImageContainer = styled.div`
	flex: 1;
	padding: 50px;
`;

const Title = styled.h1`
	font-size: 70px;
`;
const Desc = styled.p``;
const Button = styled.button``;

const Image = styled.div`
	height: 80%;
`;

const InfoContainer = styled.div`
	flex: 1;
`;

const Slider = () => {
	return (
		<Container>
			<Arrow direction="left">
				<ArrowLeftOutlined />
			</Arrow>
			<Wrapper>
				<Slide>
					<ImageContainer>
						<Image src="https://img.elo7.com.br/product/original/34F50B3/painel-adesivo-loja-suplementos-academia-decorativo-whey-adesivo-parede.jpg" />
					</ImageContainer>
					<InfoContainer>
						<Title>PROMOÇÂO DIA DAS MÃES</Title>
						<Desc>
							SUPLEMENTOS MUSCULAR COM ATÉ 30% DE DESCONTO! CORRA AGORA E NÃO PERCA
							ESSA OFERTA!!
						</Desc>
						<Button>Mostrar</Button>
					</InfoContainer>
				</Slide>
			</Wrapper>
			<Arrow direction="right">
				<ArrowRightOutlined />
			</Arrow>
		</Container>
	);
};

export default Slider;
